mapper.select:
        SELECT


--         级联表的字段
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney
        ,daijia.daijia_uuid_number as daijiaUuidNumber
        ,daijia.daijia_name as daijiaName
        ,daijia.daijia_phone as daijiaPhone
        ,daijia.daijia_id_number as daijiaIdNumber
        ,daijia.daijia_email as daijiaEmail
        ,daijia.daijia_photo as daijiaPhoto
        ,daijia.daijia_jialing as daijiaJialing
        ,daijia.daijia_old_money as daijiaOldMoney
        ,daijia.daijia_new_money as daijiaNewMoney
        ,daijia.daijia_clicknum as daijiaClicknum
        ,daijia.daijia_content as daijiaContent

        FROM daijia_order  a
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
        left JOIN daijia daijia ON a.daijia_id = daijia.id



                and a.id in

                #{item}



                and a.daijia_order_uuid_number like CONCAT('%',#{params.daijiaOrderUuidNumber},'%')


                and (
                    a.daijia_id = #{params.daijiaId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                  and a.buy_licheng >= #{params.buyLichengStart}   


                  and a.buy_licheng <= #{params.buyLichengEnd}   


                  and a.daijia_order_true_price >= #{params.daijiaOrderTruePriceStart}   


                  and a.daijia_order_true_price <= #{params.daijiaOrderTruePriceEnd}   


                and a.daijia_order_types = #{params.daijiaOrderTypes}


                and a.daijia_order_payment_types = #{params.daijiaOrderPaymentTypes}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   



                and a.daijia_id IS NOT NULL


                and daijia.daijia_uuid_number like CONCAT('%',#{params.daijiaUuidNumber},'%')


                and daijia.daijia_name like CONCAT('%',#{params.daijiaName},'%')


                and daijia.daijia_phone like CONCAT('%',#{params.daijiaPhone},'%')


                and daijia.daijia_id_number like CONCAT('%',#{params.daijiaIdNumber},'%')


                and daijia.daijia_email like CONCAT('%',#{params.daijiaEmail},'%')


                and daijia.daijia_jialing like CONCAT('%',#{params.daijiaJialing},'%')


                  and daijia.daijia_old_money >= #{params.daijiaOldMoneyStart}   


                  and daijia.daijia_old_money <= #{params.daijiaOldMoneyEnd}   


                  and daijia.daijia_new_money >= #{params.daijiaNewMoneyStart}   


                  and daijia.daijia_new_money <= #{params.daijiaNewMoneyEnd}   


                  and daijia.daijia_clicknum >= #{params.daijiaClicknumStart}   


                  and daijia.daijia_clicknum <= #{params.daijiaClicknumEnd}   


                and daijia.daijia_clicknum = #{params.daijiaClicknum}


                and daijia.daijia_content like CONCAT('%',#{params.daijiaContent},'%')



        order by a.${params.orderBy} desc
